        console.log(setPokemons());
        //const newIndex=("00"+(index+1)).slice(-3);
        //const image=`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${newIndex}.svg`
      });
  }, []);*/
  return;
}

export default Pokedex;
